import NoteContext from "./noteContext";
import { useState } from "react";

const NoteState = (props) => {

  const host = "http://localhost:5000"


  const notesInitial = [];

  const [notes, setnotes] = useState(notesInitial);

  // Get All Notes
  const fetchallNotes = async () => {

    const response = await fetch(`${host}/api/notes/fetchallnotes`,
      {
        method: "GET",
        headers: {
          "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjNlOGU4NWExYmJhNWM1Y2Y5MmU2YWYwIn0sImlhdCI6MTY3NjIxODc0OX0.pZUMLGHz98KnIComESUChvtGtrI8eiV05e-2FFPS6l8",
          "Content-Type": "application/json"
        }
      })

    const json = await response.json();
    setnotes(json)
    console.log(json);

  }

  // Add Note
  const addNote = async (title, description, tag) => {

    const response = await fetch(`${host}/api/notes/addnote`,
      {
        method: "POST",
        headers: {
          "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjNlOGU4NWExYmJhNWM1Y2Y5MmU2YWYwIn0sImlhdCI6MTY3NjIxODc0OX0.pZUMLGHz98KnIComESUChvtGtrI8eiV05e-2FFPS6l8",
          "Content-Type": "application/json"
        },
        body: JSON.stringify({ title, description, tag }),
      })
    const json = response.json();


    let note = {
      "_id": "63e91df9dc397354edde0438",
      "user": "63e8e85a1bba5c5cf92e6af0",
      "title": title,
      "description": description,
      "tag": "default",
      "date": "2023-02-12T17:11:21.829Z",
      "__v": 0
    }

    setnotes(notes.concat(note))
  }

  const deleteNote = async (id) => {

    const newNotes = notes.filter((note) => { return note._id !== id });
    setnotes(newNotes);

    const response = await fetch(`${host}/api/notes/deletenote/${id}`,
      {
        method: "DELETE",
        headers: {
          "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjNlOGU4NWExYmJhNWM1Y2Y5MmU2YWYwIn0sImlhdCI6MTY3NjIxODc0OX0.pZUMLGHz98KnIComESUChvtGtrI8eiV05e-2FFPS6l8",
          "Content-Type": "application/json"
        }
      })

    const json = await response.json();
    console.log(json);
    
  }

  // Edit Note
  const editNote = async (id, title, description, tag) => {

    const response = await fetch(`${host}/api/notes/updatenote/${id}`,
      {
        method: "PUT",
        headers: {
          "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjNlOGU4NWExYmJhNWM1Y2Y5MmU2YWYwIn0sImlhdCI6MTY3NjIxODc0OX0.pZUMLGHz98KnIComESUChvtGtrI8eiV05e-2FFPS6l8",
          "Content-Type": "application/json"
        },
        body: JSON.stringify(title, description, tag),
      })
    const json = response.json();

    //Frontend update
    for (const key in notes) {
      if (notes[key]._id === id) {
        notes[key].title = title;
        notes[key].description = description;
        notes[key].tag = tag;
      }
    }

  }

  return (
    <NoteContext.Provider value={{ notes, addNote, deleteNote, editNote, fetchallNotes }}>
      {props.children}
    </NoteContext.Provider>
  )
}

export default NoteState;
