import React from 'react'
import { useState } from 'react';
import { useContext } from 'react';
import noteContext from '../contexts/notes/noteContext';

function AddNote() {
    const context=useContext(noteContext);
    const {addNote}=context;

    const [note, setnote] = useState({title:"",desc:"",tag:"default"})

    const handleClick=()=>{
         addNote(note.title,note.desc);
    }

    const changeHandler=(e)=>{
       setnote({...note,[e.target.name]:e.target.value});
    }
    return (
        <div className="containe my-3">
            <h2>Add a note</h2>
            <div className="mb-3">
                <label htmlFor="title" className="form-label">Title</label>
                <input type="text" className="form-control" name='title' id="title" placeholder="Today's note" />
            </div>
            <div className="mb-3">
                <label htmlFor="desc" className="form-label">Description</label>
                <textarea className="form-control" name='desc' onChange={changeHandler} id="desc" rows="3"></textarea>
            </div>
            <button type='submit' className="btn btn-primary" onClick={handleClick}>Submit</button>
        </div>
    )
}

export default AddNote
