import React from 'react'
import { useContext, useEffect, useRef ,useState} from 'react';
import noteContext from '../contexts/notes/noteContext';
import NoteItem from './NoteItem';

function Notes() {
    let i = 0;
    const context = useContext(noteContext);
    const { notes, fetchallNotes } = context;
    const ref = useRef(null)

    useEffect(() => {
        fetchallNotes();
    }, [])

    const [note, setnote] = useState({etitle:"",edesc:"",etag:""})

    const updateNote = (currentnote) => {
        ref.current.click();
        setnote(currentnote)
    }
    

    const changeHandler=(e)=>{
       setnote({...note,[e.target.name]:e.target.value});
    }

    return (
        <>
            <button type="button" className="btn btn-primary d-none" ref={ref} data-bs-toggle="modal" data-bs-target="#exampleModal">
                Launch demo modal
            </button>
            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h1 className="modal-title fs-5" id="exampleModalLabel">Edit Note</h1>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <div className="mb-3">
                                <label htmlFor="etitle" className="form-label">Title</label>
                                <input type="text" className="form-control" name='etitle' id="etitle" onChange={changeHandler} placeholder="Today's note" />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="tag" className="form-label">Tag</label>
                                <input type="text" className="form-control" name='etag' id="etag" onChange={changeHandler} />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="desc" className="form-label">Description</label>
                                <textarea className="form-control" name='edesc' onChange={changeHandler} id="edesc" rows="3"></textarea>
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary">Update Note</button>
                        </div>
                    </div>
                </div>
            </div>
            <div className='row my-3'>
                <h2>Your Notes</h2>
                {notes.map((note) => {
                    return <NoteItem key={i++} note={note} updateNote={updateNote(note)} />
                })}
            </div>
        </>
    )
}

export default Notes
